package com.dddzer.printer;

import static org.apache.commons.lang.StringUtils.isBlank;

import javax.inject.Named;
import javax.inject.Singleton;

import java.util.Locale;

import com.dddzer.domain.User;
import com.dddzer.printer.DiscoverablePrinter;

/**
 * {@link org.springframework.format.Printer} for {@link User} 
 *
 * @see org.springframework.format.Printer
 * @see DiscoverablePrinter
 * @see TypeAwarePrinter
 */
@Named
@Singleton
public class UserPrinter extends DiscoverablePrinter<User> {
    public UserPrinter() {
        super(User.class);
    }

    @Override
    public String print(User user, Locale locale) {
        if (user == null) {
            return "";
        }
        StringBuilder ret = new StringBuilder(256);
        if (!isBlank(user.getFirstName())) {
            if (ret.length() != 0) {
                ret.append('/');
            }
            ret.append(user.getFirstName().trim());
        }
        if (!isBlank(user.getLastName())) {
            if (ret.length() != 0) {
                ret.append('/');
            }
            ret.append(user.getLastName().trim());
        }
        return ret.toString();
    }
}
