/**
 * Provides server-side pagination for search.
 * TODO: dependencies wiring after deserialization (get inspiration from http://jira.springframework.org/browse/SWF-1224 )
 */
@Named
@Scope("conversation")
public class AccountLazyDataModel extends GenericLazyDataModel<Account, Integer, AccountSearchForm> {
    private static final long serialVersionUID = 1L;

    @Inject
    transient private AccountRepository accountRepository;
    @Inject
    transient private AccountJsfConverter accountConverter;
    @Inject
    transient private AccountSearchForm accountSearchForm;

    private Account[] selectedRows;

    @Override
    protected Repository<Account, Integer> getRepository() {
        return accountRepository;
    }

    @Override
    protected Converter getConverter() {
        return accountConverter;
    }

    @Override
    protected GenericSearchForm<Account, AccountSearchForm> getSearchForm() {
        return accountSearchForm;
    }

    @Override
    protected SearchParameters populateSearchParameters(SearchParameters sp, int first, int pageSize, String sortField, SortOrder sortOrder,
            Map<String, String> filters) {
        super.populateSearchParameters(sp, first, pageSize, sortField, sortOrder, filters);
        if (!sp.hasOrders()) {
            sp.addOrderBy(new OrderBy("login"));
        }
        return sp;
    }

    @Override
    protected ConversationContext<Account> getSelectedContext(Account selected) {
        if (selected.isIdSet()) {
            // just the id matters as we want to reload it in the conversation entity manager.
            return AccountController.newEditContext(selected.getId());
        } else {
            return AccountController.newEditContext(selected); // fresh entity (creation)
        }
    }

    // -----------------------------------
    // Multi selection support
    // -----------------------------------

    public void setSelectedRows(Account[] selectedRows) {
        this.selectedRows = selectedRows;
    }

    public Account[] getSelectedRows() {
        return selectedRows;
    }

    public String multiSelect() {
        return getCurrentConversation() //
                .<ConversationContext<Account>> getCurrentContext() //
                .getCallBack() //
                .selected(Arrays.asList(selectedRows));
    }
}