package com.dddzer.web.domain;

import java.util.List;
import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;
import javax.servlet.http.HttpServletRequest;
import com.dddzer.dao.support.SearchParameters;
import com.dddzer.domain.FaqQuestion;
import com.dddzer.repository.FaqQuestionRepository;
import com.dddzer.web.conversation.Conversation;
import com.dddzer.web.conversation.ConversationContext;
import com.dddzer.web.conversation.ConversationFactory;

/**
 * Stateless controller for FaqQuestion conversation start. Provides also auto-complete support. 
 */
@Named
@Singleton
public class FaqQuestionController implements ConversationFactory {
    public final static String editUri = "/domain/faqQuestionEdit.faces";
    public final static String selectUri = "/domain/faqQuestionSelect.faces";
    private FaqQuestionRepository faqQuestionRepository;

    @Inject
    public void setFaqQuestionRepository(FaqQuestionRepository faqQuestionRepository) {
        this.faqQuestionRepository = faqQuestionRepository;
    }

    // --------------------------------
    // ConversationFactoryImpl
    // --------------------------------

    @Override
    public boolean canCreateConversation(HttpServletRequest request) {
        return selectUri.equals(request.getServletPath());
    }

    @Override
    public Conversation createConversation(HttpServletRequest request) {
        String uri = request.getServletPath();
        if (selectUri.equals(uri)) {
            Conversation conversation = Conversation.newInstance(request);
            ConversationContext<FaqQuestion> ctx = newSearchContext();
            ctx.setLabelWithKey("faqQuestion");
            conversation.setNextContext(ctx);
            return conversation;
        }

        throw new IllegalStateException("Unexpected conversation creation demand");
    }

    // --------------------------------
    // Autocomplete support
    // --------------------------------

    /**
     * This method is used from primefaces autocomplete components.
     */
    public List<FaqQuestion> complete(String value) {
        SearchParameters sp = new SearchParameters().anywhere().searchPattern(value);
        return faqQuestionRepository.find(sp);
    }

    // --------------------------------
    // Helper 
    // --------------------------------    

    /**
     * Helper to construct a new ConversationContext to edit an FaqQuestion.
     * @param faqQuestion the entity to edit.
     */
    public static ConversationContext<FaqQuestion> newEditContext(final FaqQuestion faqQuestion) {
        ConversationContext<FaqQuestion> ctx = new ConversationContext<FaqQuestion>();
        ctx.setEntity(faqQuestion); // used by GenericEditForm.init()
        ctx.setViewUri(editUri);
        ctx.addSourceIgnoringUseConversationEntityManager("form:account");
        return ctx;
    }

    /**
     * Helper to construct a new ConversationContext to edit an FaqQuestion.
     * @param id the id of the entity to edit.
     */
    public static ConversationContext<FaqQuestion> newEditContext(final Integer id) {
        ConversationContext<FaqQuestion> ctx = new ConversationContext<FaqQuestion>();
        ctx.setEntityId(id); // used by GenericEditForm.init()
        ctx.setViewUri(editUri);
        ctx.addSourceIgnoringUseConversationEntityManager("form:account");
        return ctx;
    }

    /**
     * Helper to construct a new ConversationContext for search/selection.
     */
    public static ConversationContext<FaqQuestion> newSearchContext() {
        ConversationContext<FaqQuestion> ctx = new ConversationContext<FaqQuestion>();
        ctx.setUseConversationEntityManager(false);
        ctx.setViewUri(selectUri);
        return ctx;
    }
}